import Card from "react-bootstrap/Card";
import Logo from "C:/Users/chesh/OneDrive/Desktop/Project2/src/Images/Logo.png";
import { faEnvelope } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

function Footer() {
  const emailAddress = "cheshtakharbanda27@gmail.com";

  return (
    <Card style={{ backgroundColor: "#DADFF7" }}>
      <div className="row justify-content-end">
        <div className="col-auto">
          <Card.Img
            src={Logo}
            alt="Card image"
            style={{ height: "200px", width: "300px" }}
            className="float-right"
          />
        </div>
      </div>

      <Card.ImgOverlay>
        <Card.Title style={{ fontFamily: "serif" }}>
          Top companies choose Udemy to train their proffesionals.
        </Card.Title>
        <Card.Text style={{ fontFamily: "serif" }}>
          Udemy is an online learning platform that offers a wide range of
          courses on various subjects. <br />
          With over 130,000 courses taught by expert instructors, Udemy provides
          opportunities for individuals to learn new skills,
          <br /> advance their careers, or explore personal interests from the
          comfort of their own homes.
        </Card.Text>
        <Card.Text>
          Contact us <br />
          <a href={`mailto:${emailAddress}`}>
            <FontAwesomeIcon icon={faEnvelope} />
          </a>
        </Card.Text>
      </Card.ImgOverlay>
    </Card>
  );
}

export default Footer;
