import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Link } from 'react-router-dom';

function Containers({ title, teacher, image ,videoUrl}) {
  const [hovered, setHovered] = useState(false);
  return (
    <Card
      className="custom-card"
      style={{
        width: "18rem",
        margin: "5px 20px 25px 35px",
        boxShadow: hovered ? "0 0 10px rgba(0, 0, 0, 0.3)" : "none", 
        transition: "box-shadow 0.3s ease", 
      }}
      onMouseEnter={() => setHovered(true)} 
      onMouseLeave={() => setHovered(false)} 
    >
      <Card.Img variant="top" src={image} style={{ width: "18rem", height: "13rem" }} />
      <Card.Body style={{ textAlign: "center" }}>
        <Card.Title>{title}</Card.Title>
        <Card.Text>{teacher}</Card.Text>
        <div className="button-container">
          <Link to={`/details?title=${title}&teacher=${teacher}&image=${image}&videoUrl=${videoUrl}`}>
            <Button variant="outline-secondary">View Details</Button>
          </Link>
        </div>
      </Card.Body>
    </Card>
  );
}

export default Containers;
