import React, { useState } from 'react';
import ListGroup from 'react-bootstrap/ListGroup';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import FormControl from 'react-bootstrap/FormControl';

function Tasks() {
  const initialItems = [];

  const [items, setItems] = useState(initialItems);
  const [newItem, setNewItem] = useState('');
  const [newColor, setNewColor] = useState('');

  const colors = ['VistaBlue', '#ED97B4', '#F3BCA5' ,'#F', '#F0E08D' ,'#EBEBD3','#C0C0AD','#949487','#686861',"#3C3C3B"];

  const handleAddItem = () => {
    if (newItem.trim() !== '' && newColor !== '') {
      setItems([...items, { name: newItem, color: newColor }]);
      setNewItem(''); // Clear the task input field after adding the item
      setNewColor(''); // Clear the color selection after adding the item
    }
  };

  return (
    <div>
      <InputGroup className="mb-3">
        <FormControl
          type="text"
          placeholder="Enter task name"
          value={newItem}
          onChange={(e) => setNewItem(e.target.value)}
        />
        <Form.Control
          as="select"
          value={newColor}
          onChange={(e) => setNewColor(e.target.value)}
        >
          <option value="">Select color</option>
          {colors.map((color, index) => (
            <option key={index} value={color}>{color}</option>
          ))}
        </Form.Control>
        <Button onClick={handleAddItem} variant="primary">Add Item</Button>
      </InputGroup>
      <ListGroup>
        {items.map((item, index) => (
          <ListGroup.Item key={index} style={{ backgroundColor: item.color }}>
            {item.name}
          </ListGroup.Item>
        ))}
      </ListGroup>
    </div>
  );
}

export default Tasks;


