import React from 'react';
import { useLocation } from 'react-router-dom';
import styled from 'styled-components';
import VideoPlayer from './components/videoPlayer.js';


const PageContainer = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;


const ContentContainer = styled.div`
  margin-top: 20px;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
`;

const TextContainer = styled.div`
  flex: 1;
  margin-right: 20px; /* Adjust the margin as needed */
`;

const SubTitle = styled.h2`
  font-size: 13px;
  color: #555;
`;

const TeacherText = styled.p`
  font-size: 10px;
  color: #777;
`;

const VideoContainer = styled.div`
  width: 600px;
  margin-top: 10px;
`;


const AnotherPage = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const title = searchParams.get('title');
  const teacher = searchParams.get('teacher');
  const videoUrl = searchParams.get('videoUrl');

  const handleVideoComplete = () => {
    console.log('Video has completed!');
    alert('Video has completed!');
  };

  return (
    <PageContainer style={{backgroundColor:"#F0F3F5"}}>
      
      {videoUrl && (
        <VideoContainer>
          <VideoPlayer videoUrl={videoUrl} onVideoComplete={handleVideoComplete} />
        </VideoContainer>
      )}
      <ContentContainer>
        <TextContainer>
          <SubTitle>{title}</SubTitle>
          <TeacherText >{teacher}</TeacherText>
        </TextContainer>
      </ContentContainer>
    </PageContainer>
  );
};

export default AnotherPage;
