import React from 'react';
import { useLocation } from 'react-router-dom';
import styled from 'styled-components';
import VideoPlayer from './components/videoPlayer.js';

const PageContainer = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.h1`
  font-size: 24px;
  color: #333;
`;

const ContentContainer = styled.div`
  margin-top: 20px;
  display: flex;
  flex-direction: row;
  align-items: flex-start;
`;

const TextContainer = styled.div`
  flex: 1;
  margin: 10px 300px 10px 10px;
`;

const SubTitle = styled.h2`
  font-size: 20px;
  color: #555;
`;

const TeacherText = styled.p`
  font-size: 16px;
  color: #777;
`;

const VideoContainer = styled.div`
  width: 600px;
  margin-top: 10px;
`;
const StyledImage = styled.img`
  width: 100%;
  max-width: 300px;
  height: auto;
  margin-top: 10px;
`;
const AnotherPage = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const title = searchParams.get('title');
  const teacher = searchParams.get('teacher');
  const image = searchParams.get('image');
  const videoUrl = searchParams.get('videoUrl');

  const handleVideoComplete = () => {
    console.log('Video has completed!');
    alert('Video has completed!');
  };

  return (
    <PageContainer>
      <Title>Your Selected Course</Title>
      <ContentContainer>
        <TextContainer>
          <SubTitle>Title: {title}</SubTitle>
          <TeacherText>Teacher: {teacher}</TeacherText>
        </TextContainer>
        <div style={{ width: '300px', height: '300px' }}>
          <StyledImage src={image} alt="" />
        </div>
      </ContentContainer>
      {videoUrl && (
        <VideoContainer>
          <h1>Video here</h1>
          <VideoPlayer videoUrl={videoUrl} onVideoComplete={handleVideoComplete} />
        </VideoContainer>
      )}
    </PageContainer>
  );
};

export default AnotherPage;




