import { Link } from "react-router-dom";
import Form from 'react-bootstrap/Form';
import Container from "react-bootstrap/Container";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import Logo from "C:/Users/chesh/OneDrive/Desktop/Project2/src/Images/Logo.png";
import Containers from "./Container.js";
import React, { useState, useContext } from 'react';
import Data from './Data';
import UserContext from './Context.js';

function NavBar() {
  const [searchQuery, setSearchQuery] = useState("");
  const filteredData = Data.filter(item =>
    item.title.toUpperCase().includes(searchQuery.toUpperCase())
  );

  function handleSearchInputChange(event) {
    setSearchQuery(event.target.value);
  }

  const { user } = useContext(UserContext);
  console.log(user);

  return (
    <div>
      <Navbar expand="lg" className="bg-body-tertiary">
        <Container fluid>
          <Navbar.Brand as={Link} to="/">
            <img src={Logo} alt="Logo" style={{ width: "200px", height: "auto" }} />
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="navbarScroll" />
          <Navbar.Collapse id="navbarScroll">
            <ul className="navbar-nav me-auto my-2 my-lg-0" style={{ maxHeight: "100px" }}>
              <li className="nav-item">
                <Link to="/" className="nav-link">Home</Link>
              </li>
              <li className="nav-item">
                <Link to="/about" className="nav-link">About Us</Link>
              </li>
              <NavDropdown title="Category" id="navbarScrollingDropdown">
                <NavDropdown.Item as={Link} className="nav-link" to="/development">Development</NavDropdown.Item>
                <NavDropdown.Item as={Link} className="nav-link" to="/personal">Personal</NavDropdown.Item>
              </NavDropdown>
            </ul>
            <Form className="d-flex" style={{marginLeft:"400px"}}>
              <Form.Control
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
                style={{ borderRadius: "20px" }}
                value={searchQuery}
                onChange={handleSearchInputChange}
              />
            </Form>
            <ul className="navbar-nav ms-auto my-2 my-lg-0" style={{ maxHeight: "100px" }}>
              {/* <li className="nav-item">
                <Link to="/learnings" className="nav-link">My Learnings</Link>
              </li> */}
              <li className="nav-item">
                <Link to="/sign" className="nav-link">SignUp</Link>
              </li>
              <li className="nav-item">
                {user && user.username ? (
                  <span className="navbar-text" style={{ textAlign: 'center', display: 'block', width: '100%', marginRight:"50px" }}>
                    Hello, {user.username}
                  </span>
                ) : null}
              </li>
            </ul>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      {searchQuery !== "" && (
        <div>
          {filteredData.map(item => (
            <Containers key={item.title} title={item.title} price={item.price} image={item.image} />
          ))}
        </div>
      )}
    </div>
  );
}

export default NavBar;
