import React, { useState,useEffect ,useContext} from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import UserContext from './components/Context';
const Signin = () => {
  const URL='http://localhost:1000/';
  const navigate = useNavigate();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [showPassword, setShowPassword] = useState(false);

  const [users,setusers]=useState();
  const { user, updateUser } = useContext(UserContext);
  

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(URL);
        setusers(response.data);
        console.log(response.data);
      } catch (err) {
    
      }
    };

    fetchData();
  }, []); 
  function isValid(){
    var loggedIn=users.find(user => user.email === email && user.password === password);
    updateUser(loggedIn);
    navigate("/");
  }
  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handlePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };



  const handleSubmit = (e) => {
    e.preventDefault();
    isValid();
  };
  
  return (
    <div style={{
      maxWidth: "400px",
      margin: "20px 0px 20px 500px",
      padding: "20px",
      border: "1px solid #ccc",
      borderRadius: "5px",
      backgroundColor: "#f9f9f9"
    }} className="signin-container">
      <h2 style={{ marginBottom: "20px" }}>Sign In</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: "20px" }} className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            placeholder="Enter your email"
            value={email}
            onChange={handleEmailChange}
            style={{
              width: "100%",
              padding: "10px",
              border: "1px solid #ccc",
              borderRadius: "5px"
            }}
            required
          />
        </div>
        <div style={{ marginBottom: "20px" }} className="form-group">
          <div className="password-input">
            <label htmlFor="password">Password:</label>
            <input
              type={showPassword ? "text" : "password"}
              id="password"
              placeholder="Enter your pass*"
              value={password}
              onChange={handlePasswordChange}
              style={{
                width: "100%",
                padding: "10px",
                border: "1px solid #ccc",
                borderRadius: "5px"
              }}
              required
            />
            <span
              className="password-toggle"
              onClick={handlePasswordVisibility}
              style={{
                position: "absolute",
                top: "52%",
                right: "520px",
                transform: "translateY(-50%)",
                cursor: "pointer"
              }}
            >
              {showPassword ? "Hide" : "Show"}
            </span>
          </div>
        </div>
        <div style={{ marginBottom: "20px" }} className="form-group1">
          <p style={{ margin: "0" }} className="form-group2">
            Don't have an account? <Link to="/sign" style={{ color: "#007bff" }}>SignUp</Link>
          </p>
        </div>
        <button
          type="submit"
          style={{
            width: "100%",
            padding: "10px",
            backgroundColor: "#007bff",
            border: "none",
            borderRadius: "5px",
            color: "#fff",
            fontSize: "16px",
            cursor: "pointer"
          }}
        >
          Sign In
        </button>
      </form>
    </div>
  );
};

export default Signin;
